/* ==============================
   RESPONSIVE BREAKPOINTS
   ============================== */

/* Tablet \(768px - 1024px\) */
@media \(max-width: 1024px\) {
    .mimi-workspace {
        grid-template-columns: 200px 1fr 260px;
    }

    .panel-right {
        display: none;
    }

    .mimi-workspace.show-right-panel .panel-right {
        display: flex;
    }

    .logo-avatar {
        width: 28px;
        height: 28px;
        font-size: 13px;
    }

    .task-item {
        padding: 6px 8px;
    }

    .chat-messages {
        padding: 12px 16px;
    }
}

/* Mobile \(<768px\) */
@media \(max-width: 768px\) {
    .mimi-outer-wrap {
        padding: 0;
        border-radius: 0;
    }

    .mimi-workspace {
        grid-template-columns: 1fr;
        grid-template-rows: auto 1fr auto;
        gap: 0;
    }

    .panel-left {
        display: none;
    }

    .mimi-workspace.show-sidebar .panel-left {
        position: fixed;
        top: 0;
        left: 0;
        bottom: 0;
        width: 280px;
        z-index: 100;
        display: flex;
    }

    .panel-center {
        border-radius: 0;
    }

    .panel-right {
        display: none;
    }

    .sidebar-toggle-btn {
        left: 12px;
        top: 12px;
    }

    /* Larger touch targets */
    .task-item,
    .new-thread-btn,
    .send-btn {
        min-height: 44px;
    }

    .chat-messages {
        padding: 10px 14px;
        gap: 10px;
    }

    .msg-user,
    .agent-bubble {
        max-width: 85%;
        padding: 12px 14px;
    }

    /* Stack status pill vertically on very small screens */
    .status-pill {
        flex-direction: column;
        gap: 4px;
        padding: 8px 16px;
    }

    /* Reduce glow sizes for mobile */
    .glow-1,
    .glow-2,
    .glow-3,
    .glow-4,
    .glow-5,
    .glow-6 {
        filter: blur\(60px\);
        opacity: 0.6;
    }
}

/* Small Mobile \(<480px\) */
@media \(max-width: 480px\) {
    .aurora-bar {
        height: 2px;
    }

    .status-pill {
        font-size: 10px;
        padding: 6px 12px;
    }

    .pill-label {
        font-size: 8px;
    }

    .chat-input-bar {
        padding: 10px;
    }

    .input-row textarea {
        font-size: 14px;
    }

    .msg-user,
    .agent-bubble {
        font-size: 13px;
    }

    .code-block {
        margin: 6px -10px;
        border-radius: 8px;
    }

    .thinking-bar {
        margin: 6px 8px;
    }
}

/* Landscape Mobile */
@media \(max-height: 500px\) and \(orientation: landscape\) {
    .chat-messages {
        padding: 8px 12px;
    }

    .status-pill-wrap {
        padding: 6px 0 2px;
    }

    .aurora-bar {
        display: none;
    }
}
EOF)

