/* ==============================
   ICON RAIL NAVIGATION \(Left Edge\)
   ============================== */
.sidebar-icon-rail {
    position: absolute;
    left: 0;
    top: 0;
    bottom: 0;
    width: 38px;
    background: rgba\(0, 0, 0, 0.3\);
    border-right: 1px solid rgba\(255, 255, 255, 0.04\);
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 14px 0;
    gap: 8px;
    z-index: 2;
}

.icon-rail-item {
    width: 32px;
    height: 32px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: rgba\(255, 255, 255, 0.3\);
    font-size: 16px;
    cursor: pointer;
    transition: all 0.2s ease;
    position: relative;
}

.icon-rail-item:hover {
    background: rgba\(255, 255, 255, 0.05\);
    color: rgba\(255, 255, 255, 0.6\);
}

.icon-rail-item.active {
    background: rgba\(0, 212, 255, 0.12\);
    color: #00d4ff;
    box-shadow: 0 0 16px rgba\(0, 212, 255, 0.6\);
}

/* Tooltip on hover */
.icon-rail-item::after {
    content: attr\(data-tooltip\);
    position: absolute;
    left: 44px;
    background: rgba\(0, 0, 0, 0.9\);
    color: #fff;
    padding: 4px 10px;
    border-radius: 6px;
    font-size: 11px;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 0.2s ease 0.5s;
    z-index: 1000;
}

.icon-rail-item:hover::after {
    opacity: 1;
}

/* Adjust panel-left to account for icon rail */
.panel-left {
    padding-left: 38px;
}

.panel-left .logo-row,
.panel-left .tasks-list,
.panel-left .sidebar-bottom {
    margin-left: 0;
}
EOF)

