/* ==============================
   PROGRESS STEPS CARD \(Fixed Bottom-Right\)
   ============================== */
.progress-card {
    position: fixed;
    bottom: 20px;
    right: 340px;
    width: 280px;
    background: rgba\(8, 12, 24, 0.85\);
    backdrop-filter: blur\(40px\);
    border: 1px solid rgba\(0, 212, 255, 0.15\);
    border-radius: 14px;
    padding: 14px 16px;
    box-shadow:
        0 8px 24px rgba\(0, 0, 0, 0.4\),
        0 0 20px rgba\(0, 212, 255, 0.08\);
    z-index: 50;
}

.progress-card-title {
    font-size: 10px;
    text-transform: uppercase;
    letter-spacing: 1.2px;
    color: rgba\(255, 255, 255, 0.4\);
    margin-bottom: 12px;
    font-weight: 600;
}

.progress-steps {
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.step {
    display: flex;
    align-items: center;
    gap: 10px;
    font-size: 12px;
}

.step-indicator {
    width: 20px;
    height: 20px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
    font-size: 10px;
}

/* Step states */
.step-done .step-indicator {
    background: rgba\(34, 197, 94, 0.15\);
    border: 2px solid #22c55e;
    color: #22c55e;
}

.step-done .step-indicator::before {
    content: 'âœ“';
}

.step-running .step-indicator {
    background: rgba\(0, 212, 255, 0.15\);
    border: 2px solid #00d4ff;
    animation: spin 1s linear infinite;
}

.step-running .step-indicator::before {
    content: '';
    width: 8px;
    height: 8px;
    border: 2px solid #00d4ff;
    border-top-color: transparent;
    border-radius: 50%;
    display: block;
}

.step-pending .step-indicator {
    background: transparent;
    border: 2px solid rgba\(255, 255, 255, 0.15\);
}

.step-awaiting .step-indicator {
    background: rgba\(0, 212, 255, 0.08\);
    border: 2px solid rgba\(0, 212, 255, 0.3\);
    animation: awaitPulse 2s ease-in-out infinite;
}

@keyframes awaitPulse {
    0%, 100% {
        box-shadow: 0 0 0 0 rgba\(0, 212, 255, 0.4\);
    }
    50% {
        box-shadow: 0 0 0 6px rgba\(0, 212, 255, 0\);
    }
}

.step-label {
    color: rgba\(255, 255, 255, 0.7\);
}

.step-done .step-label {
    color: rgba\(255, 255, 255, 0.5\);
}

.step-running .step-label {
    color: #00d4ff;
    font-weight: 500;
}

.step-status {
    margin-left: auto;
    font-size: 10px;
    color: rgba\(255, 255, 255, 0.3\);
}

/* Mobile adjustment */
@media \(max-width: 1024px\) {
    .progress-card {
        right: 20px;
        width: 240px;
    }
}

@media \(max-width: 768px\) {
    .progress-card {
        display: none;
    }
}
EOF)

